import React, { useState, useEffect, useContext, useRef, useCallback } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Fab,
  Chip,
  Alert,
} from '@mui/material';
import { Add as AddIcon, Analytics as MetricsIcon } from '@mui/icons-material';
import { UserContext } from '../contexts/UserContext';

const GardenCanvas = () => {
  const canvasRef = useRef(null);
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedPlant, setSelectedPlant] = useState(null);
  const [showAddPlant, setShowAddPlant] = useState(false);
  const [showMetrics, setShowMetrics] = useState(false);
  const [canvasSize] = useState({ width: 800, height: 600 });

  // Enhanced drag state with threshold detection
  const [isDragging, setIsDragging] = useState(false);
  const [draggedPlant, setDraggedPlant] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [mouseDownPos, setMouseDownPos] = useState({ x: 0, y: 0 });
  const [mouseDownPlant, setMouseDownPlant] = useState(null);
  const [hasDraggedBeyondThreshold, setHasDraggedBeyondThreshold] = useState(false);

  const DRAG_THRESHOLD = 5; // pixels

  const [newPlant, setNewPlant] = useState({
    name: '',
    species: '',
    imageUrl: '',
    x: 0,
    y: 0
  });

  const [newMetrics, setNewMetrics] = useState({
    date: new Date().toISOString().split('T')[0],
    ec: '',
    ph: '',
    temperature: '',
    humidity: '',
    notes: ''
  });

  const { token } = useContext(UserContext);

  const defaultPlantImages = [
    { name: 'Tomato', url: 'https://via.placeholder.com/50x50/4caf50/ffffff?text=ðŸ…' },
    { name: 'Lettuce', url: 'https://via.placeholder.com/50x50/66bb6a/ffffff?text=ðŸ¥¬' },
    { name: 'Basil', url: 'https://via.placeholder.com/50x50/8bc34a/ffffff?text=ðŸŒ¿' },
    { name: 'Pepper', url: 'https://via.placeholder.com/50x50/ff7043/ffffff?text=ðŸŒ¶ï¸' },
    { name: 'Spinach', url: 'https://via.placeholder.com/50x50/4caf50/ffffff?text=ðŸ¥¬' },
  ];

  const fetchPlants = useCallback(async () => {
    try {
      const response = await fetch('/api/plants', {
        headers: { 'Authorization': `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setPlants(data);
      } else {
        setError('Failed to fetch plants');
      }
    } catch (err) {
      setError('Error connecting to server');
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    if (token) {
      fetchPlants();
    }
  }, [token, fetchPlants]);

  const drawCanvas = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');

    // Clear canvas
    ctx.fillStyle = '#f1f8e9';
    ctx.fillRect(0, 0, canvasSize.width, canvasSize.height);

    // Draw grid
    ctx.strokeStyle = '#e0e0e0';
    ctx.lineWidth = 1;
    for (let x = 0; x <= canvasSize.width; x += 50) {
      ctx.beginPath();
      ctx.moveTo(x, 0);
      ctx.lineTo(x, canvasSize.height);
      ctx.stroke();
    }
    for (let y = 0; y <= canvasSize.height; y += 50) {
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(canvasSize.width, y);
      ctx.stroke();
    }

    // Draw plants
    plants.forEach(plant => {
      if (plant.x !== undefined && plant.y !== undefined) {
        // Highlight dragged plant
        const isBeingDragged = draggedPlant?.id === plant.id;
        const isSelected = selectedPlant?.id === plant.id;

        // Draw plant circle with different colors for different states
        if (isBeingDragged) {
          ctx.fillStyle = '#2196f3'; // Blue when dragging
        } else if (isSelected) {
          ctx.fillStyle = '#ff9800'; // Orange when selected
        } else {
          ctx.fillStyle = '#4caf50'; // Green normally
        }

        ctx.beginPath();
        ctx.arc(plant.x, plant.y, 25, 0, 2 * Math.PI);
        ctx.fill();

        // Draw plant name
        ctx.fillStyle = '#ffffff';
        ctx.font = '12px Arial';
        ctx.textAlign = 'center';
        ctx.fillText(plant.name, plant.x, plant.y + 4);

        // Draw drag indicator for dragged plant
        if (isBeingDragged) {
          ctx.strokeStyle = '#1976d2';
          ctx.lineWidth = 2;
          ctx.setLineDash([5, 5]);
          ctx.beginPath();
          ctx.arc(plant.x, plant.y, 30, 0, 2 * Math.PI);
          ctx.stroke();
          ctx.setLineDash([]);
        }
      }
    });
  }, [plants, selectedPlant, draggedPlant, canvasSize.width, canvasSize.height]);

  useEffect(() => {
    if (!loading) {
      drawCanvas();
    }
  }, [loading, drawCanvas]);

  const getMousePos = (event) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    return {
      x: event.clientX - rect.left,
      y: event.clientY - rect.top
    };
  };

  const findPlantAtPosition = (x, y) => {
    return plants.find(plant => {
      const distance = Math.sqrt((x - plant.x) ** 2 + (y - plant.y) ** 2);
      return distance <= 25;
    });
  };

  const updatePlantPosition = async (plantId, newX, newY) => {
    try {
      const response = await fetch(`/api/plants/${plantId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ x: newX, y: newY }),
      });

      if (response.ok) {
        // Update local state
        setPlants(prev => prev.map(plant => 
          plant.id === plantId 
            ? { ...plant, x: newX, y: newY }
            : plant
        ));
      } else {
        setError('Failed to update plant position');
        // Revert to original position by refetching
        fetchPlants();
      }
    } catch (err) {
      setError('Error updating plant position');
      fetchPlants();
    }
  };

  const handleMouseDown = (event) => {
    const mousePos = getMousePos(event);
    const clickedPlant = findPlantAtPosition(mousePos.x, mousePos.y);

    if (clickedPlant) {
      // Start dragging
      setIsDragging(true);
      setDraggedPlant(clickedPlant);
      setDragOffset({
        x: mousePos.x - clickedPlant.x,
        y: mousePos.y - clickedPlant.y
      });

      // Change cursor to indicate dragging
      const canvas = canvasRef.current;
      canvas.style.cursor = 'grabbing';

      // Track initial mouse position and clicked plant
      setMouseDownPos(mousePos);
      setMouseDownPlant(clickedPlant);
    }
  };

  const handleMouseMove = (event) => {
    const mousePos = getMousePos(event);
    const canvas = canvasRef.current;

    if (isDragging && draggedPlant) {
      // Update plant position in real-time during drag
      const newX = Math.max(25, Math.min(canvasSize.width - 25, mousePos.x - dragOffset.x));
      const newY = Math.max(25, Math.min(canvasSize.height - 25, mousePos.y - dragOffset.y));

      // Update local state for immediate visual feedback
      setPlants(prev => prev.map(plant => 
        plant.id === draggedPlant.id 
          ? { ...plant, x: newX, y: newY }
          : plant
      ));
    } else {
      // Update cursor based on what's under the mouse
      const plantUnderMouse = findPlantAtPosition(mousePos.x, mousePos.y);
      canvas.style.cursor = plantUnderMouse ? 'grab' : 'crosshair';
    }
  };

  const handleMouseUp = async (event) => {
    if (isDragging && draggedPlant) {
      const mousePos = getMousePos(event);
      const newX = Math.max(25, Math.min(canvasSize.width - 25, mousePos.x - dragOffset.x));
      const newY = Math.max(25, Math.min(canvasSize.height - 25, mousePos.y - dragOffset.y));

      // Save the new position to the backend
      await updatePlantPosition(draggedPlant.id, newX, newY);

      setIsDragging(false);
      setDraggedPlant(null);
      setDragOffset({ x: 0, y: 0 });

      const canvas = canvasRef.current;
      canvas.style.cursor = 'crosshair';
    }
  };

  const handleCanvasClick = (event) => {
    // Don't handle clicks if we just finished dragging
    if (isDragging) return;

    const mousePos = getMousePos(event);
    const clickedPlant = findPlantAtPosition(mousePos.x, mousePos.y);

    if (clickedPlant) {
      setSelectedPlant(clickedPlant);
      setShowMetrics(true);
    } else {
      // Add new plant at clicked position
      setNewPlant(prev => ({ ...prev, x: mousePos.x, y: mousePos.y }));
      setShowAddPlant(true);
    }
  };

  const handleAddPlant = async () => {
    try {
      const response = await fetch('/api/plants', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          name: newPlant.name,
          species: newPlant.species,
          x: newPlant.x,
          y: newPlant.y,
          'image-url': newPlant.imageUrl,
          'planting-date': new Date().toISOString().split('T')[0]
        }),
      });

      if (response.ok) {
        setShowAddPlant(false);
        setNewPlant({ name: '', species: '', imageUrl: '', x: 0, y: 0 });
        fetchPlants();
      } else {
        setError('Failed to add plant');
      }
    } catch (err) {
      setError('Error adding plant');
    }
  };

  const handleAddMetrics = async () => {
    if (!selectedPlant) return;

    try {
      const response = await fetch(`/api/plants/${selectedPlant.id}/metrics`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          'plant-id': selectedPlant.id,
          date: newMetrics.date,
          ec: parseFloat(newMetrics.ec) || null,
          ph: parseFloat(newMetrics.ph) || null,
          temperature: parseFloat(newMetrics.temperature) || null,
          humidity: parseFloat(newMetrics.humidity) || null,
          notes: newMetrics.notes
        }),
      });

      if (response.ok) {
        setShowMetrics(false);
        setNewMetrics({
          date: new Date().toISOString().split('T')[0],
          ec: '', ph: '', temperature: '', humidity: '', notes: ''
        });
        setSelectedPlant(null);
      } else {
        setError('Failed to add metrics');
      }
    } catch (err) {
      setError('Error adding metrics');
    }
  };

  if (loading) return <Typography>Loading garden...</Typography>;

  return (
    <Box>
      <Paper elevation={1} sx={{ p: 3, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom sx={{ color: 'primary.dark' }}>
          ðŸŒ± Interactive Garden Canvas
        </Typography>
        <Typography variant="body1" color="textSecondary">
          Click anywhere to add a plant, drag plants to move them, or click on existing plants to log daily metrics
        </Typography>
        <Box sx={{ mt: 2 }}>
          <Chip label="ðŸ’¡ Click empty space to add plants" variant="outlined" sx={{ mr: 1 }} />
          <Chip label="ðŸ–±ï¸ Drag plants to move them around" variant="outlined" sx={{ mr: 1 }} />
          <Chip label="ðŸ“Š Click plants to log EC, pH, temp, humidity" variant="outlined" />
        </Box>
      </Paper>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Paper elevation={2} sx={{ p: 2, display: 'inline-block' }}>
        <canvas
          ref={canvasRef}
          width={canvasSize.width}
          height={canvasSize.height}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onClick={handleCanvasClick}
          style={{
            border: '2px solid #4caf50',
            borderRadius: '8px',
            cursor: 'crosshair',
            backgroundColor: '#f1f8e9'
          }}
        />
      </Paper>

      {/* Add Plant Dialog */}
      <Dialog open={showAddPlant} onClose={() => setShowAddPlant(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Add Plant at ({Math.round(newPlant.x)}, {Math.round(newPlant.y)})</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Plant Name"
            fullWidth
            variant="outlined"
            value={newPlant.name}
            onChange={(e) => setNewPlant({ ...newPlant, name: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            label="Species"
            fullWidth
            variant="outlined"
            value={newPlant.species}
            onChange={(e) => setNewPlant({ ...newPlant, species: e.target.value })}
            sx={{ mb: 2 }}
          />
          <FormControl fullWidth margin="dense" sx={{ mb: 2 }}>
            <InputLabel>Plant Type (optional)</InputLabel>
            <Select
              value={newPlant.imageUrl}
              onChange={(e) => setNewPlant({ ...newPlant, imageUrl: e.target.value })}
            >
              {defaultPlantImages.map((img, index) => (
                <MenuItem key={index} value={img.url}>
                  {img.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowAddPlant(false)}>Cancel</Button>
          <Button onClick={handleAddPlant} variant="contained">Add Plant</Button>
        </DialogActions>
      </Dialog>

      {/* Daily Metrics Dialog */}
      <Dialog open={showMetrics} onClose={() => setShowMetrics(false)} maxWidth="md" fullWidth>
        <DialogTitle>
          Log Daily Metrics - {selectedPlant?.name}
          <Typography variant="body2" color="textSecondary">
            Position: ({selectedPlant?.x}, {selectedPlant?.y})
          </Typography>
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                margin="dense"
                label="Date"
                type="date"
                fullWidth
                variant="outlined"
                InputLabelProps={{ shrink: true }}
                value={newMetrics.date}
                onChange={(e) => setNewMetrics({ ...newMetrics, date: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                margin="dense"
                label="EC (Electrical Conductivity)"
                fullWidth
                variant="outlined"
                placeholder="e.g., 1.2"
                value={newMetrics.ec}
                onChange={(e) => setNewMetrics({ ...newMetrics, ec: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                margin="dense"
                label="pH Level"
                fullWidth
                variant="outlined"
                placeholder="e.g., 6.5"
                value={newMetrics.ph}
                onChange={(e) => setNewMetrics({ ...newMetrics, ph: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                margin="dense"
                label="Temperature (Â°C)"
                fullWidth
                variant="outlined"
                placeholder="e.g., 24"
                value={newMetrics.temperature}
                onChange={(e) => setNewMetrics({ ...newMetrics, temperature: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                margin="dense"
                label="Humidity (%)"
                fullWidth
                variant="outlined"
                placeholder="e.g., 65"
                value={newMetrics.humidity}
                onChange={(e) => setNewMetrics({ ...newMetrics, humidity: e.target.value })}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                margin="dense"
                label="Notes"
                fullWidth
                multiline
                rows={3}
                variant="outlined"
                placeholder="Any observations about the plant today..."
                value={newMetrics.notes}
                onChange={(e) => setNewMetrics({ ...newMetrics, notes: e.target.value })}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowMetrics(false)}>Cancel</Button>
          <Button onClick={handleAddMetrics} variant="contained" startIcon={<MetricsIcon />}>
            Log Metrics
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default GardenCanvas;
