import React, { useState, useEffect, useContext } from 'react';
import {
  Box,
  Paper,
  Text,
  Button,
  Modal,
  TextInput,
  Grid,
  Alert,
  Card,
  Group,
  Stack,
  ActionIcon,
  Textarea,
  Center,
} from '@mantine/core';
import { IconPlus, IconNotebook } from '@tabler/icons-react';
import { UserContext } from '../contexts/UserContext';

const GardenLogs = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [open, setOpen] = useState(false);
  const [newLog, setNewLog] = useState({
    date: new Date().toISOString().split('T')[0],
    activity: '',
    notes: '',
    plants: '',
    weather: ''
  });
  const { token } = useContext(UserContext);

  const fetchLogs = async () => {
    try {
      const response = await fetch('/api/garden-logs', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setLogs(data);
      } else {
        setError('Failed to fetch garden logs');
      }
    } catch (err) {
      setError('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (token) {
      fetchLogs();
    }
  }, [token]);

  const handleAddLog = async () => {
    try {
      const response = await fetch('/api/garden-logs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(newLog),
      });

      if (response.ok) {
        setOpen(false);
        setNewLog({
          date: new Date().toISOString().split('T')[0],
          activity: '',
          notes: '',
          plants: '',
          weather: ''
        });
        fetchLogs(); // Refresh the list
      } else {
        setError('Failed to add garden log');
      }
    } catch (err) {
      setError('Error adding garden log');
    }
  };

  if (loading) return <Text>Loading garden logs...</Text>;

  return (
    <Box>
      <Paper elevation={1} sx={{ p: 3, mb: 4 }}>
        <Text variant="h4" component="h1" gutterBottom sx={{ color: 'primary.dark' }}>
          üìù Garden Logs
        </Text>
        <Text variant="body1" color="textSecondary">
          Track your gardening activities and observations
        </Text>
      </Paper>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {logs.map((log) => (
          <Grid item xs={12} md={6} key={log.id}>
            <Card elevation={2}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <IconNotebook sx={{ color: 'secondary.main', mr: 1 }} />
                    <Text variant="h6" component="h2">
                      {log.activity}
                    </Text>
                  </Box>
                  <Text variant="body2" color="textSecondary">
                    {new Date(log.date).toLocaleDateString()}
                  </Text>
                </Box>

                {log.plants && (
                  <Box sx={{ mb: 1 }}>
                    <Chip 
                      label={`Plants: ${log.plants}`} 
                      size="small" 
                      color="primary" 
                      variant="outlined"
                    />
                  </Box>
                )}

                {log.weather && (
                  <Box sx={{ mb: 1 }}>
                    <Chip 
                      label={`Weather: ${log.weather}`} 
                      size="small" 
                      color="secondary" 
                      variant="outlined"
                    />
                  </Box>
                )}

                {log.notes && (
                  <Text variant="body2" sx={{ mt: 2 }}>
                    {log.notes}
                  </Text>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {logs.length === 0 && !loading && (
        <Paper sx={{ p: 4, textAlign: 'center', mt: 4 }}>
          <IconNotebook sx={{ fontSize: 60, color: 'secondary.light', mb: 2 }} />
          <Text variant="h6" gutterBottom>
            No garden logs yet!
          </Text>
          <Text color="textSecondary">
            Start tracking your gardening activities.
          </Text>
        </Paper>
      )}

      <ActionIcon
        color="secondary"
        aria-label="add log"
        sx={{ position: 'fixed', bottom: 16, right: 16 }}
        onClick={() => setOpen(true)}
      >
        <IconPlus />
      </ActionIcon>

      <Modal
        opened={open}
        onClose={() => setOpen(false)}
        size="sm"
        fullWidth
      >
        <Modal.Header>
          <Modal.Title>Add Garden Log</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <TextInput
            label="Date"
            type="date"
            fullWidth
            variant="outlined"
            InputLabelProps={{ shrink: true }}
            value={newLog.date}
            onChange={(e) => setNewLog({ ...newLog, date: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextInput
            label="Activity"
            fullWidth
            variant="outlined"
            placeholder="e.g., Watering, Pruning, Fertilizing"
            value={newLog.activity}
            onChange={(e) => setNewLog({ ...newLog, activity: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextInput
            label="Plants Involved"
            fullWidth
            variant="outlined"
            placeholder="e.g., Tomatoes, Basil"
            value={newLog.plants}
            onChange={(e) => setNewLog({ ...newLog, plants: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextInput
            label="Weather"
            fullWidth
            variant="outlined"
            placeholder="e.g., Sunny, Rainy, Cloudy"
            value={newLog.weather}
            onChange={(e) => setNewLog({ ...newLog, weather: e.target.value })}
            sx={{ mb: 2 }}
          />
          <Textarea
            label="Notes"
            fullWidth
            placeholder="Additional observations or notes"
            value={newLog.notes}
            onChange={(e) => setNewLog({ ...newLog, notes: e.target.value })}
          />
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={() => setOpen(false)}>Cancel</Button>
          <Button onClick={handleAddLog} variant="contained">
            Add Log
          </Button>
        </Modal.Footer>
      </Modal>
    </Box>
  );
};

export default GardenLogs;
