import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { Paper, Typography, Box, Container } from '@mui/material';

const Login = ({ onLogin }) => {
  const handleSuccess = (credentialResponse) => {
    console.log('Login successful:', credentialResponse);
    onLogin(credentialResponse.credential);
  };

  const handleError = () => {
    console.log('Login failed');
  };

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper elevation={3} sx={{ padding: 4, width: '100%' }}>
          <Box sx={{ textAlign: 'center', mb: 3 }}>
            <Typography component="h1" variant="h3" sx={{ mb: 2, color: 'primary.main' }}>
              ðŸŒ± Welcome to Horti
            </Typography>
            <Typography variant="h6" color="textSecondary">
              Your personal garden companion
            </Typography>
            <Typography variant="body1" sx={{ mt: 2, mb: 4 }}>
              Track your plants, log garden activities, and grow your green thumb!
            </Typography>
          </Box>

          <Box sx={{ display: 'flex', justifyContent: 'center' }}>
            <GoogleLogin
              onSuccess={handleSuccess}
              onError={handleError}
              size="large"
              theme="outline"
              shape="rectangular"
            />
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default Login;
