import React, { useState, useEffect, useContext } from 'react';
import {
  Box,
  Paper,
  Text,
  Button,
  Modal,
  TextInput,
  Grid,
  Select,
  Alert,
  Card,
  Group,
  Stack,
  Badge,
} from '@mantine/core';
import { IconPlus, IconPlant } from '@tabler/icons-react';
import { UserContext } from '../contexts/UserContext';

const PlantList = () => {
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [open, setOpen] = useState(false);
  const [newPlant, setNewPlant] = useState({
    name: '',
    species: '',
    plantingDate: '',
    notes: ''
  });
  const { token } = useContext(UserContext);

  const fetchPlants = async () => {
    try {
      const response = await fetch('/api/plants', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setPlants(data);
      } else {
        setError('Failed to fetch plants');
      }
    } catch (err) {
      setError('Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (token) {
      fetchPlants();
    }
  }, [token]);

  const handleAddPlant = async () => {
    try {
      const response = await fetch('/api/plants', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(newPlant),
      });

      if (response.ok) {
        setOpen(false);
        setNewPlant({ name: '', species: '', plantingDate: '', notes: '' });
        fetchPlants(); // Refresh the list
      } else {
        setError('Failed to add plant');
      }
    } catch (err) {
      setError('Error adding plant');
    }
  };

  if (loading) return <Text>Loading plants...</Text>;

  return (
    <Box>
      <Paper elevation={1} sx={{ p: 3, mb: 4 }}>
        <Text variant="h4" component="h1" gutterBottom sx={{ color: 'primary.dark' }}>
          ðŸŒ± My Plants
        </Text>
        <Text variant="body1" color="textSecondary">
          Manage your plant collection and track their growth
        </Text>
      </Paper>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {plants.map((plant) => (
          <Grid item xs={12} sm={6} md={4} key={plant.id}>
            <Card elevation={2}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <IconPlant sx={{ color: 'primary.main', mr: 1 }} />
                  <Text variant="h6" component="h2">
                    {plant.name}
                  </Text>
                </Box>
                <Text color="textSecondary" gutterBottom>
                  Species: {plant.species}
                </Text>
                {plant.plantingDate && (
                  <Text color="textSecondary" gutterBottom>
                    Planted: {new Date(plant.plantingDate).toLocaleDateString()}
                  </Text>
                )}
                {plant.notes && (
                  <Text variant="body2">
                    {plant.notes}
                  </Text>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {plants.length === 0 && !loading && (
        <Paper sx={{ p: 4, textAlign: 'center', mt: 4 }}>
          <IconPlant sx={{ fontSize: 60, color: 'primary.light', mb: 2 }} />
          <Text variant="h6" gutterBottom>
            No plants yet!
          </Text>
          <Text color="textSecondary">
            Start your garden by adding your first plant.
          </Text>
        </Paper>
      )}

      <Fab
        color="primary"
        aria-label="add plant"
        sx={{ position: 'fixed', bottom: 16, right: 16 }}
        onClick={() => setOpen(true)}
      >
        <IconPlus />
      </Fab>

      <Modal opened={open} onClose={() => setOpen(false)} maxWidth="sm" fullWidth>
        <Modal.Header>
          <Modal.Title>Add New Plant</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <TextInput
            autoFocus
            label="Plant Name"
            fullWidth
            value={newPlant.name}
            onChange={(e) => setNewPlant({ ...newPlant, name: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextInput
            label="Species"
            fullWidth
            value={newPlant.species}
            onChange={(e) => setNewPlant({ ...newPlant, species: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextInput
            label="Planting Date"
            type="date"
            fullWidth
            value={newPlant.plantingDate}
            onChange={(e) => setNewPlant({ ...newPlant, plantingDate: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextInput
            label="Notes"
            fullWidth
            multiline
            rows={3}
            value={newPlant.notes}
            onChange={(e) => setNewPlant({ ...newPlant, notes: e.target.value })}
          />
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={() => setOpen(false)}>Cancel</Button>
          <Button onClick={handleAddPlant} variant="contained">
            Add Plant
          </Button>
        </Modal.Footer>
      </Modal>
    </Box>
  );
};

export default PlantList;
